def longest_palindrome_length(s):
    n = len(s)
    dp = [[0 for _ in range(n)] for _ in range(n)]
    max_length = 0

    for gap in range(n):
        for i in range(n - gap):
            j = i + gap
            if gap == 0:
                dp[i][j] = 1
            elif s[i] == s[j] and (j - i < 2 or dp[i + 1][j - 1] == j - i):
                dp[i][j] = j - i + 1
                max_length = max(max_length, dp[i][j])

